int patternCount(string* text, string* pattern) {
	int count = 0;
	int patternLength = pattern->length();

	for (int i = 0; i < text->length(); i++) {
		string subText = text->substr(i, patternLength);
		if (subText.compare(*pattern) == 0)
			count++;
	}
	return count;
}



void frequentWords(string *text, int k, unordered_map<string, int>& mapFreqWord) {

	// get all the patterns
	for (int i = 0; i < text->length() - k; i++) {
		string pattern = text->substr(i, k);

		// only do count if pattern is not there
		unordered_map<string, int>::iterator iFind = mapFreqWord.find(pattern);
		if (iFind != mapFreqWord.end()) {

		}
		else {
			int count = patternCount(text, &pattern);
			mapFreqWord.emplace(pattern, count);
		}





		//pair<unordered_map<string, int>::iterator,bool> aPair =  mapFreqWord.emplace(pattern,count);
	}

	// iterate to all map, get max count
	int maxCount = 0;
	list<string> listMaxPattern;

	for (unordered_map<string, int>::iterator it = mapFreqWord.begin(); it != mapFreqWord.end(); it++) {
		if (it->second > maxCount) {
			maxCount = it->second;

			listMaxPattern.clear(); // delete all previous less than

			listMaxPattern.push_back(it->first);
		}
		else if (it->second == maxCount) {
			listMaxPattern.push_back(it->first);
		}
	}

	//cout << maxCount << endl;
	ofstream myfile;
	myfile.open("output.txt");

	for (list<string>::iterator it = listMaxPattern.begin(); it != listMaxPattern.end(); it++) {
		myfile << *it << " ";
	}

	myfile.close();
	//cout << endl;

}

